mapper.select:
    select id,area_code code,name
    from sys_area
    where status = 0

	  and id = #{id}


      and level_code = #{levelCode}


      and parent_code = #{parentCode}


      and area_code = #{areaCode}


      and zip_code = #{zipCode}


      and city_code = #{cityCode}


      and name = #{name}


      and short_name = #{shortName}


      and merger_name = #{mergerName}


      and pinyin = #{pinyin}


      and lng > #{lng} -1 and lng < #{lng}+1


      and lat > #{lat} -1 and lat < #{lat}+1

	SELECT a.area_code province_code, a.short_name province, b.area_code city_code, b.short_name city,
	  c.area_code county_code, c.short_name county
	FROM sys_area a
	INNER JOIN sys_area b ON a.area_code=b.parent_code
	INNER JOIN sys_area c ON b.area_code=c.parent_code
	WHERE a.status = 0

	  and a.id = #{id}


	  and a.level_code = #{levelCode}


  		and a.parent_code = #{parentCode}


  		and e.area_code = #{areaCode}


  		and a.name = #{name}


  		and e.short_name = #{shortName}


  		and e.pinyin = #{pinyin}


  		and e.lng > #{lng} -1 and e.lng < #{lng}+1


  		and e.lat > #{lat} -1 and e.lat < #{lat}+1
